sys_script_include.script:
var TableTestUtils = Class.create();
TableTestUtils.prototype = {
	initialize: function() {
	},

	currentFactory: function(tableName, fieldName, order) {
		var currentRecords = new GlideRecord(tableName);
		currentRecords.addNotNullQuery(fieldName);
		currentRecords.setLimit(1);
		gs.print(order.type);
		if (order.type == 'descending') {
			currentRecords.orderByDesc(order.field);
		}
		else {
			currentRecords.orderBy(order.field);
		}
		currentRecords.query();
		currentRecords.next();
		gs.print(currentRecords.getRowCount());
		return currentRecords;
	},


	type: 'TableTestUtils'
};
